mapper.select:
SELECT
	COUNT(*)
FROM orders
WHERE mem_num = #{mem_num}


		AND o_num = #{keyword}


		AND o_name LIKE '%' || #{keyword} || '%'

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM orders WHERE mem_num=#{mem_num}
AND o_num = #{keyword}
AND o_name LIKE '%' || #{keyword} || '%'
ORDER BY o_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM orders o JOIN member m ON o.mem_num = m.mem_num
o_num = #{keyword}
id LIKE '%' || #{keyword} || '%'
o_name LIKE '%' || #{keyword} || '%'
ORDER BY o_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}
