mapper.update:
UPDATE product SET
	p_quantity = p_quantity - #{p_quantity}
WHERE p_num = #{p_num}

UPDATE product SET
p_photo=#{p_photo}, p_photoname=#{p_photoname},
p_name=#{p_name}, p_cont1=#{p_cont1}, p_cont2=#{p_cont2}, p_brand=#{p_brand}, p_price=#{p_price}, p_dprice=#{p_dprice}, p_quantity=#{p_quantity}, p_status=#{p_status}, p_category=#{p_category}, modify_date=SYSDATE WHERE p_num=#{p_num}
